package com.company.Utilities.GUI;//TODO: mobve to Utilities.GUI pkgimport net.miginfocom.swing.MigLayout;import javax.swing.*;import javax.swing.border.Border;import java.awt.*;public class Utils {//title wrapper with standard single line border //JPanel object assigns each panel's setName by combining //title  string parameter passed at the call time and "_panel" //     public static JPanel coatPanelBorder(String title, JComponent jc) {        JPanel pc = new JPanel(new MigLayout(""));        pc.setName(title + "_panel");        pc.add(jc);        pc.setBorder(BorderFactory.createTitledBorder(Settings.simple_line_border, title));        pc.setBackground(Settings.default_background);        pc.validate();        return pc;    }    public static JPanel[] checkbox_array(String[] titles) {        JCheckBox[] jcb = new JCheckBox[titles.length];        JPanel[] jcp = new JPanel[titles.length];        for (int i = 0; i < titles.length; i++) {            jcb[i] = new JCheckBox(titles[i]);            jcb[i].setName("checkbox_" + titles + "_" + i);            jcb[i].setBackground(Settings.default_background);            jcp[i] = new JPanel(new MigLayout("insets 0"));            jcp[i].add(jcb[i]);            jcp[i].setBorder(BorderFactory.createMatteBorder(2, 2, 2, 2, Settings.default_background));        }        return jcp;    }//overload :1//TODO:  move all Look&Fill values to the db and reference 'em    public static JPanel coatPanelBorder(String title, JComponent[] jc, String mig) {        JPanel pc = new JPanel(new MigLayout(mig));        pc.setName(title + "_panel");        for (int i = 0; i < jc.length; i++) {            pc.add(jc[i]);        }        pc.setBorder(BorderFactory.createTitledBorder(Settings.simple_line_border, title));        pc.setBackground(Settings.default_background);        pc.validate();        return pc;    }    public static JPanel createPanelTextField(String title, int columns) {        JTextField tf = new JTextField(columns);        JPanel pan = new JPanel();        if (title.length() > 0) {            pan.setBorder(BorderFactory.createTitledBorder(BorderFactory.createLineBorder(Settings.default_border_color), title));        }        tf.setBackground(new Color(250, 250, 250));        tf.setBorder(BorderFactory.createEmptyBorder(1, 1, 1, 1));        pan.setBackground(Settings.default_background);        pan.add(tf);        pan.validate();        return pan;    }//TODO: Rename//TODO: try other  jcomponents//set ups for Jlist's individual string looks and behaves propspublic static ListCellRenderer get_list_renderer(int font_size) {        final int font_s = font_size;        ListCellRenderer renderer = new ListCellRenderer() {            @Override            public Component getListCellRendererComponent(JList list, Object value, int index, boolean isSelected, boolean cellHasFocus) {                JLabel label = new JLabel();                JPanel k = new JPanel(new MigLayout("", ""));                label.setText(value.toString());                if (isSelected) {                    k.setBackground(new Color(216, 218, 216));                    label.setForeground(Color.BLACK);                    label.setFont(new Font("PT Sans", Font.BOLD, font_s));                    k.setBorder(BorderFactory.createTitledBorder(BorderFactory.createMatteBorder(1, 3, 1, 1, Color.black), ""));                } else {                    k.setBackground(Color.WHITE);                    label.setForeground(Color.black);                    label.setFont(new Font("PT Sans", Font.BOLD, font_s));                    k.setBorder(BorderFactory.createMatteBorder(0, 3, 0, 0, Color.lightGray));                }                k.add(label, "");                return k;            }        };        return renderer;    }    public static ListCellRenderer get_combo_renderer() {        ListCellRenderer renderer = new ListCellRenderer() {            @Override            public Component getListCellRendererComponent(JList list, Object value, int index, boolean isSelected, boolean cellHasFocus) {                JLabel label = new JLabel();                JPanel k = new JPanel(new MigLayout("", ""));                label.setText(value.toString());                label.setForeground(Color.BLACK);                if (isSelected) {                    k.setBackground(Color.white);                    label.setForeground(Color.BLACK);                    label.setFont(new Font("PT Sans", Font.BOLD, 12));                } else {                    k.setBackground(Color.WHITE);                    label.setFont(new Font("PT Sans", Font.PLAIN, 12));                }                k.add(label, "");                return k;            }        };        return renderer;    }//GUI Utilities: methods for readable interface construction    public  static JList createList(String[] list_array, int font_size) {        JList rList = new JList(list_array);        rList.setCellRenderer(get_list_renderer(font_size));        rList.setVisibleRowCount(4);        rList.setAutoscrolls(true);        return rList;    }    public static JPanel createComboPanel(String[] str_arr, int count, String str_flow) {        JPanel p_JB = new JPanel(new MigLayout("", "", "30"));        int x = 0;        while (x < count) {            JComboBox jbox = new JComboBox(str_arr);            jbox.setName("combo_" + x);            jbox.setRenderer(get_combo_renderer());            p_JB.add(jbox, "wrap");            x++;        }        p_JB.setBackground(Settings.default_background);        p_JB.validate();        return p_JB;    }    public  static JLabel createLabel(String text) {        JLabel label = new JLabel(text);        label.setFont(new Font("PT Sans", Font.BOLD, 13));        label.setVerticalAlignment(SwingUtilities.CENTER);        return label;    }    public static JTextField createTextField(String title, int columns) {        JTextField tf = new JTextField(columns);        if (title.length() > 0) {            tf.setBorder(BorderFactory.createTitledBorder(title));        }        return tf;    }}